import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const BookList = () => {
  const { user } = useAuth();
  const navigate = useNavigate();

  const [books, setBooks] = useState([]);
  const [pagination, setPagination] = useState({ totalPages: 0, currentPage: 1, hasPrev: false, hasNext: false });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBooks = async (page = 1) => {
    console.log('fetchBooks called with page:', page);
    try {
      setLoading(true);
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/books?page=${page}`);
      console.log('Books fetched:', response.data.books.length);
      setBooks(response.data.books);
      const { page: currentPage, totalPages } = response.data;
      setPagination({
        totalPages,
        currentPage,
        hasPrev: currentPage > 1,
        hasNext: currentPage < totalPages
      });
      setError(null);
    } catch (err) {
      console.error('Error fetching books:', err);
      setError('Failed to fetch books');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    console.log('BookList useEffect called');
    fetchBooks();
  }, []);

  const handlePageChange = (page) => {
    fetchBooks(page);
  };

  const handleDelete = async (bookId) => {
    if (!window.confirm('Are you sure you want to delete this book?')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/books/${bookId}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setBooks(prevBooks => prevBooks.filter(book => book._id !== bookId));
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to delete book');
    }
  };

  const handleEdit = (bookId) => {
    navigate(`/edit-book/${bookId}`);
  };

  if (loading) return <div className="text-center text-gray-600 mt-10">Loading...</div>;
  if (error) return <div className="text-center text-red-600 mt-10">{error}</div>;

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-4xl font-extrabold text-gray-900 mb-8">Books</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {books.map(book => (
          <div
            key={book._id}
            className="bg-white rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 p-6 flex flex-col justify-between"
          >
            <div>
              <h2 className="text-2xl font-semibold text-gray-800 mb-2">{book.title}</h2>
              <p className="text-gray-600 mb-1">by <span className="font-medium">{book.author}</span></p>
              <p className="text-gray-500 mb-3 italic">{book.genre} - {book.year}</p>
            </div>
            <div className="flex space-x-3 mt-4">
              <Link
                to={`/books/${book._id}`}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white text-center py-2 rounded-md font-semibold transition-colors"
              >
                View Details
              </Link>
              {user && user.id === book.addedBy._id.toString() && (
                <>
                  <button
                    onClick={() => handleEdit(book._id)}
                    className="flex-1 bg-yellow-500 hover:bg-yellow-600 text-white py-2 rounded-md font-semibold transition-colors"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(book._id)}
                    className="flex-1 bg-red-600 hover:bg-red-700 text-white py-2 rounded-md font-semibold transition-colors"
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
      {pagination.totalPages > 1 && (
        <div className="flex justify-center mt-10 space-x-4">
          {pagination.hasPrev && (
            <button
              onClick={() => handlePageChange(pagination.currentPage - 1)}
              className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md text-gray-800 font-semibold transition-colors"
            >
              Previous
            </button>
          )}
          <span className="px-4 py-2 text-gray-700 font-medium">
            Page {pagination.currentPage} of {pagination.totalPages}
          </span>
          {pagination.hasNext && (
            <button
              onClick={() => handlePageChange(pagination.currentPage + 1)}
              className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md text-gray-800 font-semibold transition-colors"
            >
              Next
            </button>
          )}
        </div>
      )}
    </div>
  );
};

export default BookList;
