import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const BookDetails = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [book, setBook] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [averageRating, setAverageRating] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newReview, setNewReview] = useState({ rating: 5, reviewText: '' });
  const [reviewError, setReviewError] = useState(null);
  const [reviewLoading, setReviewLoading] = useState(false);

  const fetchBookDetails = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/books/${id}`);
      setBook(response.data.book);
      setReviews(response.data.reviews);
      setAverageRating(response.data.averageRating);
      setError(null);
    } catch (err) {
      setError('Failed to fetch book details');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBookDetails();
  }, [id]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    setReviewLoading(true);
    setReviewError(null);
    try {
      await axios.post(`/api/reviews/${id}`, newReview);
      setNewReview({ rating: 5, reviewText: '' });
      fetchBookDetails(); // Refresh data
    } catch (err) {
      setReviewError(err.response?.data?.message || 'Failed to add review');
    } finally {
      setReviewLoading(false);
    }
  };

  const handleDeleteReview = async (reviewId) => {
    if (!window.confirm('Are you sure you want to delete this review?')) return;
    try {
      await axios.delete(`/api/reviews/${reviewId}`);
      fetchBookDetails();
    } catch (err) {
      alert('Failed to delete review');
    }
  };

  if (loading) return <div className="text-center">Loading...</div>;
  if (error) return <div className="text-center text-red-600">{error}</div>;
  if (!book) return <div className="text-center">Book not found</div>;

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white p-6 rounded shadow mb-8">
        <h1 className="text-3xl font-bold mb-2">{book.title}</h1>
        <p className="text-xl text-gray-600 mb-2">by {book.author}</p>
        <p className="text-gray-500 mb-4">{book.genre} - {book.year}</p>
        <p className="mb-4">{book.description}</p>
        <p className="text-lg font-semibold mb-4">Average Rating: {averageRating} / 5</p>
        {user && user.id === book.addedBy._id && (
          <Link
            to={`/edit-book/${book._id}`}
            className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded mr-2"
          >
            Edit Book
          </Link>
        )}
      </div>

      <div className="bg-white p-6 rounded shadow mb-8">
        <h2 className="text-2xl font-bold mb-4">Reviews ({reviews.length})</h2>
        {reviews.map(review => (
          <div key={review._id} className="border-b border-gray-200 pb-4 mb-4 last:border-b-0">
            <div className="flex justify-between items-start">
              <div>
                <p className="font-semibold">{review.userId.name}</p>
                <p className="text-yellow-500">{'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}</p>
                <p className="mt-2">{review.reviewText}</p>
              </div>
              {user && user.id === review.userId._id && (
                <button
                  onClick={() => handleDeleteReview(review._id)}
                  className="text-red-600 hover:text-red-800"
                >
                  Delete
                </button>
              )}
            </div>
          </div>
        ))}
      </div>

      {user && (
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-2xl font-bold mb-4">Add Review</h2>
          {reviewError && <div className="mb-4 text-red-600">{reviewError}</div>}
          <form onSubmit={handleReviewSubmit} className="space-y-4">
            <div>
              <label className="block mb-1 font-semibold">Rating</label>
              <select
                value={newReview.rating}
                onChange={(e) => setNewReview(prev => ({ ...prev, rating: parseInt(e.target.value) }))}
                className="border border-gray-300 rounded px-3 py-2"
              >
                {[1, 2, 3, 4, 5].map(num => (
                  <option key={num} value={num}>{num}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="block mb-1 font-semibold">Review</label>
              <textarea
                value={newReview.reviewText}
                onChange={(e) => setNewReview(prev => ({ ...prev, reviewText: e.target.value }))}
                required
                minLength={10}
                rows={4}
                className="w-full border border-gray-300 rounded px-3 py-2"
                placeholder="Write your review here..."
              />
            </div>
            <button
              type="submit"
              disabled={reviewLoading}
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:opacity-50"
            >
              {reviewLoading ? 'Submitting...' : 'Submit Review'}
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default BookDetails;
