import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Profile = () => {
  const { user } = useAuth();
  const [books, setBooks] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUserData = async () => {
    try {
      setLoading(true);
      const booksResponse = await axios.get('/api/books');
      const userBooks = booksResponse.data.books.filter(book => book.addedBy._id === user.id);
      setBooks(userBooks);

      const reviewsResponse = await axios.get('/api/reviews');
      const userReviews = reviewsResponse.data.reviews.filter(review => review.userId._id === user.id);
      setReviews(userReviews);

      setError(null);
    } catch (err) {
      setError('Failed to fetch profile data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      fetchUserData();
    }
  }, [user]);

  if (loading) return <div className="text-center">Loading...</div>;
  if (error) return <div className="text-center text-red-600">{error}</div>;

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Profile</h1>
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">My Books</h2>
        {books.length === 0 ? (
          <p>You have not added any books yet.</p>
        ) : (
          <ul className="space-y-4">
            {books.map(book => (
              <li key={book._id} className="bg-white p-4 rounded shadow">
                <Link to={`/books/${book._id}`} className="text-blue-600 hover:underline">
                  {book.title}
                </Link>
                <p className="text-gray-600">by {book.author}</p>
              </li>
            ))}
          </ul>
        )}
      </section>
      <section>
        <h2 className="text-2xl font-semibold mb-4">My Reviews</h2>
        {reviews.length === 0 ? (
          <p>You have not written any reviews yet.</p>
        ) : (
          <ul className="space-y-4">
            {reviews.map(review => (
              <li key={review._id} className="bg-white p-4 rounded shadow">
                <p>
                  <strong>Book:</strong>{' '}
                  <Link to={`/books/${review.bookId}`} className="text-blue-600 hover:underline">
                    {review.bookId.title}
                  </Link>
                </p>
                <p>
                  <strong>Rating:</strong> {review.rating} / 5
                </p>
                <p>{review.reviewText}</p>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
};

export default Profile;
